// Site ID extrahieren
const siteId = $input.first().json.id;

// Listen-Definitionen
const lists = [
  {
    name: 'nza-kpl',
    displayName: 'NZA Hauptliste',
    description: 'Zentrale NZA-Liste mit Stammdaten, Prozessen und Kosten',
    template: 'genericList'
  },
  {
    name: 'nza-massnahmen',
    displayName: 'NZA Maßnahmen',
    description: 'Korrektur- und Vorbeugemaßnahmen zu NZA-Vorgängen',
    template: 'genericList'
  },
  {
    name: 'nza-bilder',
    displayName: 'NZA Bilder',
    description: 'Fehlerbilder und Dokumentation',
    template: 'genericList'
  },
  {
    name: 'nza-mitarbeiter',
    displayName: 'NZA Mitarbeiter',
    description: 'Produktions-Mitarbeiter für Verursacher-Lookup',
    template: 'genericList'
  },
  {
    name: 'nza-config',
    displayName: 'NZA Konfiguration',
    description: 'System-Einstellungen und Minutensätze',
    template: 'genericList'
  }
];

return lists.map(list => ({
  json: {
    siteId: siteId,
    ...list
  }
}));

// Ergebnisse sammeln
const results = $input.all().map(item => ({
  listName: item.json.displayName || item.json.name,
  listId: item.json.id,
  status: item.json.id ? 'created' : 'error'
}));

return [{ json: { results, count: results.length } }];
