const items = $input.first().json.value || [];
const query = $('Webhook').first().json.query || {};

let mitarbeiter = items
  .filter(item => item.fields?.Aktiv === true || item.fields?.Aktiv === 'true')
  .map(item => {
    const f = item.fields || {};
    return {
      id: item.id,
      kuerzel: f.Kuerzel,
      name: f.Title,
      abteilung: f.Abteilung,
      kst: f.KST,
      funktion: f.Funktion,
      email: f.Email || '',
      personalnummer: f.Personalnummer,
      ospUser: f.OSP_User === true || f.OSP_User === 'true'
    };
  })
  .sort((a, b) => a.name.localeCompare(b.name));

// Filter nach KST wenn angegeben
if (query.kst) {
  mitarbeiter = mitarbeiter.filter(m => m.kst === query.kst || m.abteilung === query.kst);
}

// Filter nach Abteilung
if (query.abteilung) {
  mitarbeiter = mitarbeiter.filter(m => m.abteilung === query.abteilung);
}

return [{ json: { mitarbeiter, count: mitarbeiter.length } }];
