const items = $input.first().json.value || [];

const massnahmen = items.map(item => {
  const f = item.fields || {};
  return {
    id: item.id,
    nzaId: f.NZA_ID,
    titel: f.Title,
    typ: f.Massnahme_Typ,
    beschreibung: f.Beschreibung,
    verantwortlichId: f.Verantwortlich_ID,
    verantwortlichName: f.Verantwortlich_Name,
    verantwortlichEmail: f.Verantwortlich_Email,
    termin: f.Termin,
    status: f.Status || 'Offen',
    erledigtAm: f.Erledigt_Am,
    wirksamkeit: f.Wirksamkeit,
    benachrichtigungTeams: f.Benachrichtigung_Teams === true,
    benachrichtigungEmail: f.Benachrichtigung_Email === true,
    erstelltAm: item.createdDateTime
  };
});

return [{ json: { massnahmen, count: massnahmen.length } }];

// POST: Neue Maßnahme anlegen
const body = $('Webhook').first().json.body;

if (!body.nzaId || !body.titel) {
  throw new Error('NZA-ID und Titel sind Pflichtfelder!');
}

return [{
  json: {
    fields: {
      Title: body.titel,
      NZA_ID: body.nzaId,
      Massnahme_Typ: body.typ || 'Sofortmaßnahme',
      Beschreibung: body.beschreibung || '',
      Verantwortlich_ID: body.verantwortlichId || '',
      Verantwortlich_Name: body.verantwortlichName || '',
      Verantwortlich_Email: body.verantwortlichEmail || '',
      Termin: body.termin || null,
      Status: 'Offen',
      Wirksamkeit: 'Nicht geprüft',
      Benachrichtigung_Teams: false,
      Benachrichtigung_Email: false
    },
    notifyTeams: body.notifyTeams === true,
    notifyEmail: body.notifyEmail === true
  }
}];

const created = $input.first().json;
const prepareData = $('Prepare POST').first().json;

return [{ json: {
  success: true,
  id: created.id,
  nzaId: created.fields.NZA_ID,
  titel: created.fields.Title,
  notifyTeams: prepareData.notifyTeams,
  notifyEmail: prepareData.notifyEmail,
  verantwortlichId: created.fields.Verantwortlich_ID,
  verantwortlichEmail: created.fields.Verantwortlich_Email,
  message: 'Maßnahme erfolgreich erstellt'
}}];

// PATCH: Maßnahme aktualisieren
const body = $('Webhook').first().json.body;
const itemId = body.id;

if (!itemId) {
  throw new Error('Item-ID fehlt!');
}

const fields = {};
if (body.status) fields.Status = body.status;
if (body.beschreibung) fields.Beschreibung = body.beschreibung;
if (body.termin) fields.Termin = body.termin;
if (body.wirksamkeit) fields.Wirksamkeit = body.wirksamkeit;
if (body.status === 'Abgeschlossen' && !body.erledigtAm) {
  fields.Erledigt_Am = new Date().toISOString();
}
if (body.erledigtAm) fields.Erledigt_Am = body.erledigtAm;

return [{ json: { itemId, fields } }];
