// GET: Kosten체bersicht zu einem NZA-Vorgang
const query = $('Webhook').first().json.query || {};

// Minutens채tze pro Kostenstelle
const MINUTENSATZ = {
  '1000': 1.98,
  '2000': 1.21,
  '3000': 0.93,
  '4000': 1.02,
  '5000': 1.02,
  'Lager': 1.10,
  'Verwaltung': 1.37
};

return [{ json: { 
  query,
  minutensaetze: MINUTENSATZ,
  info: 'Nutze POST um Kosten zu berechnen'
}}];

// POST: Kosten berechnen
const body = $('Webhook').first().json.body;

// Minutens채tze pro Kostenstelle (aus CLAUDE.md / AV_AGK)
const MINUTENSATZ = {
  '1000': 1.98,  // Fertigung F1
  '2000': 1.21,  // Fertigung F2
  '3000': 0.93,  // Fertigung F3
  '4000': 1.02,  // Fertigung F4
  '5000': 1.02,  // Fertigung F5
  'Lager': 1.10,
  'Verwaltung': 1.37
};

// Prozesse verarbeiten
const prozesse = body.prozesse || [];
let kostenProzesse = 0;
const prozessDetails = [];

prozesse.forEach((p, idx) => {
  const zeit = parseFloat(p.zeit) || 0;
  const kst = p.kst || '1000';
  const faktor = MINUTENSATZ[kst] || MINUTENSATZ['1000'];
  const kosten = Math.round(zeit * faktor * 100) / 100;
  kostenProzesse += kosten;

  prozessDetails.push({
    nr: idx + 1,
    prozess: p.prozess || '',
    werker: p.werker || '',
    kst: kst,
    zeit: zeit,
    minutensatz: faktor,
    kosten: kosten
  });
});

// Zus채tzliche Kosten
const kostenMaterial = parseFloat(body.kostenMaterial) || 0;
const kostenSonstige = parseFloat(body.kostenSonstige) || 0;

// Gesamtkosten
const kostenGesamt = Math.round((kostenProzesse + kostenMaterial + kostenSonstige) * 100) / 100;

return [{ json: {
  nzaId: body.nzaId || null,
  berechnung: {
    prozesse: prozessDetails,
    kostenProzesse: Math.round(kostenProzesse * 100) / 100,
    kostenMaterial: kostenMaterial,
    kostenSonstige: kostenSonstige,
    kostenGesamt: kostenGesamt
  },
  minutensaetze: MINUTENSATZ
}}];
