const items = $input.first().json.value || [];
const query = $('Webhook').first().json.query || {};

const now = new Date();
const currentYear = now.getFullYear();
const currentMonth = now.getMonth();

// Zeitfilter
const filterYear = query.year ? parseInt(query.year) : currentYear;

// Statistiken initialisieren
let offeneNza = 0;
let kostenYtd = 0;
let kostenMtd = 0;
let kostenGesamt = 0;
let anzahlGesamt = 0;
let anzahlYtd = 0;
let anzahlMtd = 0;

const typVerteilung = { 'Nacharbeit': 0, 'Neufertigung': 0, 'Ausschuss': 0, 'Sonstiges': 0 };
const kstVerteilung = {};
const monatsVerteilung = {};
const fehlerursachen = {};

items.forEach(item => {
  const f = item.fields || {};
  const datum = new Date(f.field_2 || item.createdDateTime);
  const itemYear = datum.getFullYear();
  const itemMonth = datum.getMonth();
  const status = f.status || 'Neu';
  const typ = f.field_1 || 'Sonstiges';
  const kst = f.field_9 || 'Unbekannt';
  const kostenItem = parseFloat(f.kosten_gesamt) || 0;
  const fehlerursache = f.Fehlerursache || 'Unbekannt';

  anzahlGesamt++;
  kostenGesamt += kostenItem;

  // Status-ZÃ¤hlung
  if (status !== 'Abgeschlossen') {
    offeneNza++;
  }

  // Jahresfilter
  if (itemYear === filterYear) {
    anzahlYtd++;
    kostenYtd += kostenItem;

    // Typ-Verteilung
    typVerteilung[typ] = (typVerteilung[typ] || 0) + 1;

    // KST-Verteilung
    kstVerteilung[kst] = (kstVerteilung[kst] || 0) + 1;

    // Monats-Verteilung
    const monatKey = `${itemYear}-${String(itemMonth + 1).padStart(2, '0')}`;
    if (!monatsVerteilung[monatKey]) {
      monatsVerteilung[monatKey] = { anzahl: 0, kosten: 0 };
    }
    monatsVerteilung[monatKey].anzahl++;
    monatsVerteilung[monatKey].kosten += kostenItem;

    // Fehlerursachen
    fehlerursachen[fehlerursache] = (fehlerursachen[fehlerursache] || 0) + 1;

    // Aktueller Monat
    if (itemMonth === currentMonth) {
      anzahlMtd++;
      kostenMtd += kostenItem;
    }
  }
});

// Top KST ermitteln
const topKst = Object.entries(kstVerteilung)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 5)
  .map(([kst, anzahl]) => ({ kst, anzahl }));

// Top Fehlerursachen
const topFehlerursachen = Object.entries(fehlerursachen)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 5)
  .map(([ursache, anzahl]) => ({ ursache, anzahl }));

// Monatstrend sortieren
const monatsTrend = Object.entries(monatsVerteilung)
  .sort((a, b) => a[0].localeCompare(b[0]))
  .map(([monat, data]) => ({ monat, ...data }));

return [{ json: {
  filter: { year: filterYear },
  kpis: {
    offeneNza,
    anzahlGesamt,
    anzahlYtd,
    anzahlMtd,
    kostenGesamt: Math.round(kostenGesamt * 100) / 100,
    kostenYtd: Math.round(kostenYtd * 100) / 100,
    kostenMtd: Math.round(kostenMtd * 100) / 100
  },
  charts: {
    typVerteilung,
    topKst,
    topFehlerursachen,
    monatsTrend
  }
}}];
