const items = $input.first().json.value || [];

const result = items.map(item => {
  const f = item.fields || {};
  return {
    id: item.id,
    nzaId: f.Title,
    typ: f.field_1,
    datum: f.field_2,
    artikel: f.field_3,
    betriebsauftrag: f.field_4,
    pruefmenge: f.field_5,
    davonNio: f.field_6,
    verursacher: f.field_7,
    verursacherPersNr: f.field_8,
    kst: f.field_9,
    qaId: f.field_10,
    beschreibung: f.field_14,
    kategorien: f.field_15 ? (Array.isArray(f.field_15) ? f.field_15 : [f.field_15]) : [],
    bemerkungen: f.field_16,
    status: f.status || 'Neu',
    kostenMaterial: f.field_65 || 0,
    kostenSonstige: f.field_17 || 0,
    kostenProzesse: f.kosten_prozesse || 0,
    kostenGesamt: f.kosten_gesamt || 0,
    prozesse: [
      { prozess: f.field_19, werker: f.field_20, kst: f.kostenstelle_1, zeit: f.field_22 },
      { prozess: f.field_24, werker: f.field_25, kst: f.kostenstelle_2, zeit: f.field_27 },
      { prozess: f.field_29, werker: f.field_30, kst: f.kostenstelle_3, zeit: f.field_32 },
      { prozess: f.field_34, werker: f.field_35, kst: f.kostenstelle_4, zeit: f.field_37 },
      { prozess: f.field_39, werker: f.field_40, kst: f.kostenstelle_5, zeit: f.field_42 }
    ].filter(p => p.prozess || p.zeit > 0)
  };
});

return [{ json: { items: result, count: result.length } }];
