const response = $input.first().json;

if (response.id) {
  return [{
    json: {
      success: true,
      message: 'SharePoint-Liste Reklamationen-2025 erfolgreich erstellt',
      listId: response.id,
      listName: response.displayName,
      webUrl: response.webUrl
    }
  }];
} else {
  return [{
    json: {
      success: false,
      message: 'Fehler beim Erstellen der Liste',
      error: response
    }
  }];
}
