// QA-ID generieren (Format: QA-JJNNN)
const now = new Date();
const year = now.getFullYear().toString().slice(-2);

// Zaehler aus Config holen (wird spaeter aus SharePoint gelesen)
// Fuer den Start: Zaehler aus Query oder Default
let counter = $input.first().json.body._counter || 1;

// QA-ID formatieren
const qaId = `QA-${year}${String(counter).padStart(3, '0')}`;

const body = $input.first().json.body;

return {
  qaId: qaId,
  counter: counter,
  fields: {
    Title: body.Title || 'Neue Reklamation',
    QA_ID: qaId,
    Rekla_Typ: body.Rekla_Typ || 'Kunde',
    Rekla_Status: 'Neu',
    Prioritaet: body.Prioritaet || 'mittel',
    Absender: body.Absender || '',
    DebKredNr: body.DebKredNr || '',
    Beschreibung: body.Beschreibung || '',
    Verantwortlich: body.Verantwortlich || '',
    Erfassungsdatum: body.Erfassungsdatum || new Date().toISOString().split('T')[0],
    // Lieferanten-Felder (optional)
    Lieferschein_Nr: body.Lieferschein_Nr || '',
    Lieferdatum: body.Lieferdatum || null,
    Artikel_Nr: body.Artikel_Nr || '',
    Beanstandete_Menge: body.Beanstandete_Menge || null
  }
};

const spResponse = $input.first().json;
const qaId = $('Prepare Data').first().json.qaId;

if (spResponse.id) {
  return {
    success: true,
    qaId: qaId,
    sharePointId: spResponse.id,
    message: `Reklamation ${qaId} erfolgreich erstellt`
  };
} else {
  return {
    success: false,
    error: 'SharePoint Item konnte nicht erstellt werden',
    details: spResponse
  };
}
